import{restaurantRating}from"../../data/fecth-api";import{configData}from"../../configuration/config";export class AboutPage extends HTMLElement{constructor(){super()}async connectedCallback(){try{const t=await restaurantRating(),a=configData.urlImgAPI,e=document.createElement("div");e.classList.add("about-list"),t.slice(0,3).forEach((t=>{const n=document.createElement("div");n.classList.add("about-container"),n.innerHTML=`\n                <img class="about-img" src="${a+t.pictureId}" alt="${t.name}>\n                <div class="about-detail>\n                    <h2 class="about-head">${t.name}</div>\n                    <p class="about-item">${t.description}\n                </div>\n                `,e.appendChild(n)})),this.appendChild(e)}catch(t){console.error(t);const a=document.createElement("p");a.textContent="Failed to load restaurant data",this.appendChild(a)}}}customElements.define("about-page",AboutPage);