import UrlParser from"../../routers/url-parser";import{getRestauranDetail,getRestaurantList}from"../../data/fecth-api";import{configData}from"../../configuration/config";export class RestoDetail extends HTMLElement{constructor(){super()}async connectedCallback(){try{const t=configData.urlImgBanner,e=UrlParser.parseActiveUrlWithoutCombiner(),a=await getRestauranDetail(e.id),r=document.createElement("div");r.classList.add("detail-item"),r.innerHTML=`\n        <div class="resto-detail">\n          <h2>${a.name}</h2>\n          <img src="${t+a.pictureId}" alt="${a.name}">\n          <p>${a.description}</p>\n        </div>\n      `,this.appendChild(r)}catch(t){console.error(t);const e=document.createElement("p");e.textContent="Failed to load restaurant data",this.appendChild(e)}}}customElements.define("resto-detail",RestoDetail);