import{restaurantRating}from"../../data/fecth-api";import{configData}from"../../configuration/config";export class RecomPage extends HTMLElement{constructor(){super()}async connectedCallback(){try{const e=await restaurantRating(),t=configData.urlImgAPI,a=document.createElement("div");a.classList.add("recom-list"),e.slice(0,4).forEach((e=>{const n=document.createElement("div");n.classList.add("recom-container"),n.innerHTML=`\n          <img class="recom-img" src="${t+e.pictureId}" alt="${e.name}">\n          <div class="recom-detail">\n            <a href="#/detail/${e.id}">${e.name}</a>\n            <p>${e.rating}</p>\n          </div>\n        `,a.appendChild(n)})),this.appendChild(a)}catch(e){console.error(e);const t=document.createElement("p");t.textContent="Failed to load restaurant data",this.appendChild(t)}}}customElements.define("recom-page",RecomPage);