import axios from"axios";import{API_GATE}from"../configuration/endpoint";export const getRestaurantList=async()=>{try{const t=`${API_GATE.restaurantList}`;return(await axios.get(t)).data.restaurants}catch(t){return console.error("Error fetching restaurant list:",t),[]}};export const restaurantRating=async()=>{try{const t=`${API_GATE.restaurantList}`;return(await axios.get(t)).data.restaurants.filter((t=>t.rating>=4.5))}catch(t){return console.error("Error fetching restaurant list:",t),[]}};export const getRestauranDetail=async t=>{const r=`${API_GATE.restaurantDetail(t)}`;return(await axios.get(r)).data.restaurant};export const addReview=async()=>{const t=`${API_GATE.restaurantReview}`;return(await axios.post(t)).data.customerReviews};