const UrlParser={parseActiveUrlWithCombiner(){const r=window.location.hash.slice(1).toLowerCase(),e=this._urlSplitter(r);return this._urlCombiner(e)},parseActiveUrlWithoutCombiner(){const r=window.location.hash.slice(1).toLowerCase();return this._urlSplitter(r)},_urlSplitter(r){const e=r.split("/");return{resource:e[1]||null,id:e[2]||null,verb:e[3]||null}},_urlCombiner:r=>(r.resource?`/${r.resource}`:"/")+(r.id?"/:id":"")+(r.verb?`/${r.verb}`:"")};export default UrlParser;